function useStats(){
    const [stats, setStats] = useState();
    useEffect(()=>{
async function fetchData(){
    console.log('Fetching Data');
    const data = await fetch('https://covid19.mathdro.id/api').then
    (res => res.json());
    setStats(data);
}

fetchData();
    }, []);                           
}

function Stats(){
    const stats = useStats();
    console.log(stats);
    return <div>
        <p>Stats</p>
    </div>

}

export default function IndexPage(){
    return <div>
        <Stats></Stats>
        </div>;
}
